CREATE DEFINER=`root`@`localhost` PROCEDURE `purchase_game`(
	IN p_account_id varchar(30),
	IN p_game_id int
)
BEGIN
	DECLARE account_balance decimal(18,2);
	DECLARE account_reward_points int;
	DECLARE already_purchased int;
	DECLARE game_price decimal(18,2);
	DECLARE game_downloads int;

	SELECT COUNT(*) INTO already_purchased FROM jocaros_db.purchase
	WHERE Id_Account=p_account_id AND Id_Game=p_game_id;

	IF already_purchased = 0
		THEN
		SELECT Balance INTO account_balance FROM jocaros_db.account WHERE Id=p_account_id;
		SELECT Reward_Points INTO account_reward_points FROM jocaros_db.account WHERE Id=p_account_id;
		SELECT Price INTO game_price FROM jocaros_db.game WHERE Id=p_game_id;
		SELECT Sales INTO game_downloads FROM jocaros_db.game WHERE Id=p_game_id;

		IF account_balance >= game_price
			THEN
			INSERT INTO jocaros_db.purchase VALUES(p_account_id, p_game_id, NOW());
			UPDATE jocaros_db.account SET Balance=(account_balance-game_price), 
			Reward_Points=account_reward_points+CAST((((game_price+0.01)/10) - 0.5) AS UNSIGNED) WHERE Id=p_account_id;
			UPDATE jocaros_db.game SET Sales=(game_downloads+1) WHERE Id=p_game_id;
			COMMIT;
			SELECT 1;
		ELSE
			SELECT 0;
		END IF;
	ELSE
		SELECT 2;
	END IF;
END ;;
